-- ============================================================
-- TIMESHEET DATA - GENERATED
-- From 2025-04-01 to 2025-10-26
-- Staff: Hoàng Lan (ID=6), Đỗ Minh (ID=7)
-- ============================================================

BEGIN;




-- Verify data
SELECT 
    u.firstname || ' ' || u.lastname as staff_name,
    COUNT(*) as total_days,
    ROUND(SUM(t.hours_worked), 2) as total_hours,
    ROUND(AVG(t.hours_worked), 2) as avg_hours_per_day
FROM TimeSheets t
JOIN Users u ON t.staff_id = u.id
WHERE t.date >= '2025-04-01' AND t.date <= '2025-10-26'
GROUP BY u.id, u.firstname, u.lastname
ORDER BY u.id;

COMMIT;

